/**
 * UserData represents the User Data Parameters(user_data) of a Server Side Event Request.
 * 'user_data' is a set of identifiers Facebook can use for targeted attribution. See Custom Audiences from CRM Data for details on how to normalize and hash the data you send.
 * @see {@link https://developers.facebook.com/docs/marketing-api/conversions-api/parameters/customer-information-parameters}
 *
 */
export interface UserData {
    /**
     * An email address, in lowercase. - must be hashed
     */
    em?: string;
    /**
     * A phone number. Include only digits with country code, area code, and number. - must be hashed
     */
    ph?: string;
    /**
     * Gender, in lowercase. Either f or m. - must be hashed
     */
    ge?: string;
    /**
     * A first name in lowercase. - must be hashed
     */
    fn?: string;
    /**
     * A last name in lowercase. - must be hashed
     */
    ln?: string;
    /**
     * A date of birth given as year, month, and day in YYYYMMDD format. - must be hashed
     */
    db?: string;
    /**
     * A city in lower-case without spaces or punctuation. - must be hashed
     */
    ct?: string;
    /**
     * A two-letter state code in lowercase. - must be hashed
     */
    st?: string;
    /**
     * A two-letter country code in lowercase. - must be hashed
     */
    zp?: string;
    /**
     * Postal code of the city in your country standard - must be hashed
     */
    country?: string;
    /**
     * Any unique ID from the advertiser. - must be hashed
     */
    external_id?: string;
    /**
     * The IP address of the browser corresponding to the event. - hashing is recommended
     */
    client_ip_address: string;
    /**
     * The user agent for the browser corresponding to the event. - do not hash
     */
    client_user_agent: string;
    /**
     * The Facebook click ID value stored in the _fbc browser cookie under your domain. - do not hash
     */
    fbc?: string;
    /**
     * The Facebook browser ID value stored in the _fbp browser cookie under your domain. - do not hash
     */
    fbp?: string;
    /**
     * The subscription ID for the user in this transaction. - do not hash
     */
    subscription_id?: string;
    /**
     * The FB login ID for the user. - do not hash
     */
    fb_login_id?: string;
    /**
     * The Id associated with a lead generated by Facebook's Lead Ads. - do not hash
     */
    lead_id?: string;
    /**
     * The date of birth day in DD format. - do not hash
     */
    dobd?: string;
    /**
     * The date of birth month in MM format. - do not hash
     */
    dobm?: string;
    /**
     * The date of birth year in YYYY format. - do not hash
     */
    doby?: string;
}
